import \'package:flutter/material.dart\';\nimport \'package:tdah_organizer/models/debt.dart\';\n\nclass DebtProvider with ChangeNotifier {\n  final List<Debt> _debts = [\n    Debt(creditor: \"Banco A\", amount: 500.00, dueDate: DateTime.now().add(const Duration(days: 10))),\n    Debt(creditor: \"Tarjeta de Crédito\", amount: 1200.50, dueDate: DateTime.now().add(const Duration(days: 20))),\n    Debt(creditor: \"Préstamo Estudiantil\", amount: 850.00, dueDate: DateTime.now().add(const Duration(days: 30))),\n  ];\n\n  List<Debt> get debts => _debts;\n\n  void addDebt(Debt debt) {\n    _debts.add(debt);\n    notifyListeners();\n  }\n}\n