import \'package:flutter/material.dart\';\nimport \'package:tdah_organizer/models/subscription.dart\';\n\nclass SubscriptionProvider with ChangeNotifier {\n  final List<Subscription> _subscriptions = [\n    Subscription(name: \"Netflix\", amount: 15.99, nextBilling: DateTime.now().add(const Duration(days: 10))),\n    Subscription(name: \"Spotify\", amount: 9.99, nextBilling: DateTime.now().add(const Duration(days: 15))),\n    Subscription(name: \"Adobe Creative Cloud\", amount: 52.99, nextBilling: DateTime.now().add(const Duration(days: 20))),\n    Subscription(name: \"Amazon Prime\", amount: 14.99, nextBilling: DateTime.now().add(const Duration(days: 5))),\n  ];\n\n  List<Subscription> get subscriptions => _subscriptions;\n\n  void addSubscription(Subscription subscription) {\n    _subscriptions.add(subscription);\n    notifyListeners();\n  }\n}\n