\'package:flutter/material.dart\';\nimport \'package:carousel_slider/carousel_slider.dart\';\nimport \'package:flutter_gen/gen_l10n/app_localizations.dart\';\nimport \'package:google_fonts/google_fonts.dart\';\n\nclass DashboardScreen extends StatelessWidget {\n  const DashboardScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    final l10n = AppLocalizations.of(context)!;\n    final quickActions = [\n      _QuickAction(icon: Icons.money_off, title: l10n.debts, color: Colors.red.shade400),\n      _QuickAction(icon: Icons.subscriptions, title: l10n.subscriptions, color: Colors.blue.shade400),\n      _QuickAction(icon: Icons.task, title: l10n.tasks, color: Colors.green.shade400),\n      _QuickAction(icon: Icons.note, title: l10n.notes, color: Colors.orange.shade400),\n      _QuickAction(icon: Icons.timer, title: l10n.pomodoroTimer, color: Colors.purple.shade400),\n    ];\n\n    return Scaffold(\n      body: SingleChildScrollView(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\n              l10n.dashboard,\n              style: GoogleFonts.oswald(fontSize: 32, fontWeight: FontWeight.bold),\n            ),\n            const SizedBox(height: 24),\n            _buildSectionTitle(context, \"Acciones RÃ¡pidas\"),\n            const SizedBox(height: 16),\n            CarouselSlider.builder(\n              itemCount: quickActions.length,\n              itemBuilder: (context, index, realIndex) {\n                final action = quickActions[index];\n                return _buildQuickActionCard(context, action);\n              },\n              options: CarouselOptions(\n                height: 120,\n                autoPlay: true,\n                autoPlayInterval: const Duration(seconds: 5),\n                enlargeCenterPage: true,\n                viewportFraction: 0.8,\n                aspectRatio: 2.0,\n              ),\n            ),\n            const SizedBox(height: 24),\n            _buildSectionTitle(context, \"Resumen Financiero\"),\n            const SizedBox(height: 16),\n            _buildSummaryCard(\n              context,\n              title: l10n.overduePayments,\n              value: \"\$1,250.00\",\n              icon: Icons.payment,\n              color: Colors.red.shade300,\n            ),\n            const SizedBox(height: 16),\n            _buildSummaryCard(\n              context,\n              title: l10n.dailyExpenses,\n              value: \"\$75.50\",\n              icon: Icons.request_page,\n              color: Colors.orange.shade300,\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildSectionTitle(BuildContext context, String title) {\n    return Text(\n      title,\n      style: GoogleFonts.roboto(fontSize: 20, fontWeight: FontWeight.w500),\n    );\n  }\n\n  Widget _buildQuickActionCard(BuildContext context, _QuickAction action) {\n    return Card(\n      elevation: 4,\n      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(16)),\n      child: Container(\n        decoration: BoxDecoration(\n          borderRadius: BorderRadius.circular(16),\n          gradient: LinearGradient(\n            colors: [action.color.withOpacity(0.7), action.color],\n            begin: Alignment.topLeft,\n            end: Alignment.bottomRight,\n          ),\n        ),\n        child: Center(\n          child: Column(\n            mainAxisAlignment: MainAxisAlignment.center,\n            children: [\n              Icon(action.icon, size: 36, color: Colors.white),\n              const SizedBox(height: 8),\n              Text(\n                action.title,\n                style: GoogleFonts.roboto(color: Colors.white, fontWeight: FontWeight.bold),\n              ),\n            ],\n          ),\n        ),\n      ),\n    );\n  }\n\n  Widget _buildSummaryCard(BuildContext context, {required String title, required String value, required IconData icon, required Color color}) {\n    return Card(\n      elevation: 4,\n      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(16)),\n      child: Container(\n        padding: const EdgeInsets.all(20),\n        decoration: BoxDecoration(\n          borderRadius: BorderRadius.circular(16),\n          color: Theme.of(context).cardColor,\n        ),\n        child: Row(\n          children: [\n            Icon(icon, size: 40, color: color),\n            const SizedBox(width: 16),\n            Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(title, style: GoogleFonts.roboto(fontSize: 16, color: Colors.white70)),\n                const SizedBox(height: 4),\n                Text(value, style: GoogleFonts.oswald(fontSize: 28, fontWeight: FontWeight.bold, color: Colors.white)),\n              ],\n            )\n          ],\n        ),\n      ),\n    );\n  }\n}\n\nclass _QuickAction {\n  final IconData icon;\n  final String title;\n  final Color color;\n\n  _QuickAction({required this.icon, required this.title, required this.color});\n}\n