\'package:flutter/material.dart\';\nimport \'package:flutter_gen/gen_l10n/app_localizations.dart\';\nimport \'package:google_fonts/google_fonts.dart\';\n\nclass TasksScreen extends StatefulWidget {\n  const TasksScreen({super.key});\n\n  @override\n  _TasksScreenState createState() => _TasksScreenState();\n}\n\nclass _TasksScreenState extends State<TasksScreen> {\n  final List<_Task> _tasks = [\n    _Task(title: \"Pagar la tarjeta de crédito\", isCompleted: false),\n    _Task(title: \"Comprar víveres\", isCompleted: true),\n    _Task(title: \"Llamar al médico\", isCompleted: false),\n    _Task(title: \"Terminar el informe del proyecto\", isCompleted: false),\n  ];\n\n  @override\n  Widget build(BuildContext context) {\n    final l10n = AppLocalizations.of(context)!;\n\n    return Scaffold(\n      body: Padding(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\n              l10n.tasks,\n              style: GoogleFonts.oswald(fontSize: 32, fontWeight: FontWeight.bold),\n            ),\n            const SizedBox(height: 24),\n            Expanded(\n              child: ListView.builder(\n                itemCount: _tasks.length,\n                itemBuilder: (context, index) {\n                  final task = _tasks[index];\n                  return _buildTaskCard(context, task);\n                },\n              ),\n            ),\n          ],\n        ),\n      ),\n      floatingActionButton: FloatingActionButton(\n        onPressed: () {\n          // TODO: Implementar la funcionalidad para añadir una nueva tarea\n        },\n        child: const Icon(Icons.add),\n        tooltip: \"Añadir Tarea\",\n      ),\n    );\n  }\n\n  Widget _buildTaskCard(BuildContext context, _Task task) {\n    return Card(\n      elevation: 4,\n      margin: const EdgeInsets.only(bottom: 16),\n      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(16)),\n      child: Container(\n        padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 12),\n        decoration: BoxDecoration(\n          borderRadius: BorderRadius.circular(16),\n          color: Theme.of(context).cardColor,\n        ),\n        child: Row(\n          children: [\n            Checkbox(\n              value: task.isCompleted,\n              onChanged: (bool? value) {\n                setState(() {\n                  task.isCompleted = value!\;\n                });\n              },\n              activeColor: Colors.green.shade400,\n              checkColor: Colors.white,\n            ),\n            const SizedBox(width: 12),\n            Expanded(\n              child: Text(\n                task.title,\n                style: GoogleFonts.roboto(\n                  fontSize: 18,\n                  color: task.isCompleted ? Colors.white54 : Colors.white,\n                  decoration: task.isCompleted ? TextDecoration.lineThrough : TextDecoration.none,\n                ),\n              ),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n\nclass _Task {\n  final String title;\n  bool isCompleted;\n\n  _Task({required this.title, required this.isCompleted});\n}\n