import \'package:flutter/material.dart\';\nimport \'package:google_fonts/google_fonts.dart\';\n\nclass AddTaskForm extends StatefulWidget {\n  final void Function(String) onAdd;\n\n  const AddTaskForm({required this.onAdd, super.key});\n\n  @override\n  _AddTaskFormState createState() => _AddTaskFormState();\n}\n\nclass _AddTaskFormState extends State<AddTaskForm> {\n  final _formKey = GlobalKey<FormState>();\n  final _titleController = TextEditingController();\n\n  @override\n  Widget build(BuildContext context) {\n    return Padding(\n      padding: const EdgeInsets.all(20.0),\n      child: Form(\n        key: _formKey,\n        child: Column(\n          mainAxisSize: MainAxisSize.min,\n          children: [\n            Text(\'Añadir Nueva Tarea\', style: GoogleFonts.oswald(fontSize: 24, fontWeight: FontWeight.bold)),\n            const SizedBox(height: 20),\n            TextFormField(\n              controller: _titleController,\n              decoration: const InputDecoration(labelText: \'Título de la Tarea\'),\n              validator: (value) {\n                if (value == null || value.isEmpty) {\n                  return \'Por favor, introduce un título\';\n                }\n                return null;\n              },\n            ),\n            const SizedBox(height: 20),\n            ElevatedButton(\n              onPressed: _submitForm,\n              child: const Text(\'Añadir Tarea\'),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n\n  void _submitForm() {\n    if (_formKey.currentState!.validate()) {\n      widget.onAdd(_titleController.text);\n      Navigator.of(context).pop();\n    }\n  }\n}\n